/* COLOR VARIABLES */
:root {
    --color-1: #a2bce0;
    --color-2: #0b5563;
    --color-3: #beb8eb;
    --color-4: #5e5c6c;
    --color-5: #f78154;
}
/* **************** */
body {
    font-family: 'Montserrat', sans-serif;
    background-color: rgb(31, 106, 172);
}

/* SECTION SEARCH HEADER*/
.search__logo-icon {
    width: 5rem;
}
.search__logo-text {
}
/* baker icon sticky top */

.search-input {
    padding: 1rem;
    background-color: #fff;
    border: 1px solid #fff;
    outline: 0;
    border-radius: 25px;
}

.search-input::placeholder {
    font-size: 1.1rem;
    color: var(--color-4);
    font-weight: 500;
}

.search-input-icon {
    background-color: #fff;
    border-color: #fff;
    cursor: pointer;
    width: 4rem;
    height: 4rem;
}

/* **************** */
/* RECIPE SECTIONS */
.recipes-section {
}

.food {
    transition: all 0.5s;
    cursor: pointer;
    max-height: 15rem;
    min-height: 15rem;
}

.food:hover {
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.5);
}

.food-title {
    color: var(--color-5);
}
.food-info {
    color: var(--color-1);
}
.food-img {
    max-height: 85px;
}

/* LOADTER */

.loader {
    width: 5rem;
    animation-name: loaderAnimation;
    animation-duration: 0.7s;
    animation-iteration-count: infinite;
}

@keyframes loaderAnimation {
    0% {
        transform: rotate(0);
    }

    100% {
        transform: rotate(360deg);
    }
}
